mapper.select:
select ahv.ID_ as id, ahv.PROC_DEF_KEY_ as procDefKey, ahv.NAME_ as name , ahv.VAR_TYPE_ as vtype, ahv.LONG_ as vlong, ahv.TEXT_ as value, ahv.CREATE_TIME_ as createTime, ahv.PROC_INST_ID_ as processInstanceId from act_hi_varinst ahv where ahv.NAME_ like '\_%'
and ahv.CREATE_TIME_ > #{latestTime}
and ahv.PROC_INST_ID_ = #{processInstanceId}
order by ahv.CREATE_TIME_ desc
